}
#categories.navbar-nav > li {
    vertical-align: top;
    position: relative;
    z-index: auto;
    padding: 0 0px 0 0;
}
#categories.navbar-nav > li:before {
    content: '|';
    display: none;
    float: right;
    line-height: 44px;
    position: relative;
    left: 8px;
    font-size: 20px;
    font-weight: 700;
}
#categories.navbar-nav > li:last-child:before {
    display: none;
}
#categories.navbar-nav > li:after {
    content: '';
    background: transparent;
    height: 1px;
    width: 80%;
    position: absolute;
    bottom: 12px;
    margin: auto;
    left: 0;
    right: 0;
}
#categories.navbar-nav > li:hover:after {
    background: #005fb0;
}

    

#categories.navbar-nav > li > a {
    font-family: "Poppins", sans-serif;
    font-size: 16px;
    font-weight: 400;
    padding: 0 18px 0 18px;
    line-height: 52px;
    color: #000;
    text-transform: uppercase;
}
