.sticky-jump .stuck{
    -webkit-animation:stuckMoveDown .6s;
    animation:stuckMoveDown .6s;
}
.sticky-fade .stuck{
    -webkit-animation:stuckFadeIn .6s;
    animation:stuckFadeIn .6s
}
